var UIBootbox=function() {
    var o=function() {
	
		//新增單元
		$(".demo_4").click(function() {
			bootbox.prompt("請輸入單元名稱(最多50字)", function(result) {
				if (result === null) {
					alert("已取消");
				} else {
					alert("新增完成");
				}
			});
		}), 
		
		
		//新增類別
		$(".demo_class").click(function() {
			bootbox.prompt("請輸入類別名稱(最多50字)", function(o) {
			null===o?alert("已取消"): alert("確認完成");
			});
		}),
		
		
		//新增第一層類別		
		$('.create_class_01').click(function(){
			bootbox.prompt("新增第一層類別", function(result) {
				if (result === null) {
					alert("已取消");
				} else {
					alert("新增完成");
				}
			});
		});
		
		//新增第二層類別		
		$('.create_class_02').click(function(){
			bootbox.prompt("新增第二層類別", function(result) {
				if (result === null) {
					alert("已取消");
				} else {
					alert("新增完成");
				}
			});
		});
		
		
		//匯入新增		
		$('.import_create').click(function(){
			var dialog = bootbox.dialog({
				title: '匯入新增',
				message: $(".form-content").html(),
				onEscape: true,
				buttons: {
					cancel: {
						label: "取消",
						callback: function(){
							alert("已取消匯入！");
						}
					},
					ok: {
						label: "確認",
						callback: function(){
							alert("匯入成功！\n已完成匯入新增，共30筆。");
						}
					}
				}
			});
		});
		
		
		//匯入刪除		
		$('.import_delete').click(function(){
			var dialog = bootbox.dialog({
				title: '匯入刪除',
				message: $(".form-content").html(),
				onEscape: true,
				buttons: {
					cancel: {
						label: "取消",
						callback: function(){
							alert("已取消匯入！");
						}
					},
					ok: {
						label: "確認",
						callback: function(){
							alert("匯入成功！\n已完成匯入刪除，共30筆。");
						}
					}
				}
			});
		});
		
		

	};
		return {
			init: function() {
			o();
			}
		}
	}();
		jQuery(document).ready(function() {
		UIBootbox.init();
	}
);
    